package org.mygov.covdashboard.controller;import org.mygov.covdashboard.exception.CovDashBoardException;import org.mygov.covdashboard.model.DashboardResponse;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.ControllerAdvice;import org.springframework.web.bind.annotation.ExceptionHandler;import java.util.ArrayList;@ControllerAdvicepublic class ApiExceptionController {	@ExceptionHandler(CovDashBoardException.class)	public ResponseEntity<DashboardResponse> handleCovDashBoardException(CovDashBoardException ex) {		DashboardResponse response = DashboardResponse.builder().entityList(new ArrayList<>())				.responseCode(HttpStatus.INTERNAL_SERVER_ERROR.toString())				.responseMessage("Error while executing query. " + ex.getMessage())				.build();		return new ResponseEntity<>(response, HttpStatus.FORBIDDEN);	}}