package org.mygov.covdashboard.utility;import org.apache.commons.lang3.StringUtils;import org.jsoup.Jsoup;import org.jsoup.nodes.Document;import org.jsoup.select.Elements;import org.springframework.scheduling.annotation.Async;import org.springframework.stereotype.Component;import java.io.IOException;import java.util.ArrayList;import java.util.List;import java.util.concurrent.CompletableFuture;@Componentpublic class HtmlParser {	public List<String> getCsvList() throws IOException {		Document document = Jsoup.connect("https://github.com/CSSEGISandData/COVID-19/tree/master/csse_covid_19_data/csse_covid_19_daily_reports").get();		Elements links = document.select("a[href]");		List<String> cvsList = new ArrayList<>();		links.forEach(link -> {			String href = link.attr("href");			if(href.endsWith("csv")  && href.startsWith("/CSSEGISandData/COVID-19/blob/master/csse_covid_19_data/csse_covid_19_daily_reports")) {				String uri =  link.attr("href");				cvsList.add(StringUtils.remove(uri, "/blob"));			}		});		return cvsList;	}}