package org.mygov.covdashboard.utility;import org.junit.jupiter.api.BeforeAll;import org.junit.jupiter.api.Test;import org.mockito.Mock;import org.mockito.Mockito;import org.mockito.MockitoAnnotations;import org.mygov.covdashboard.exception.CovDashBoardException;import org.mygov.covdashboard.model.CSVDataModel;import org.springframework.boot.test.mock.mockito.MockBean;import org.springframework.test.context.event.annotation.BeforeTestMethod;import java.util.ArrayList;import java.util.List;import static org.junit.jupiter.api.Assertions.assertEquals;import static org.junit.jupiter.api.Assertions.assertNotNull;class CsvParserTest {	@MockBean	private CsvParser csvParser;	@BeforeTestMethod	public void initMocks(){		MockitoAnnotations.initMocks(this);	}	@Test	void buildCsvDataModel() throws CovDashBoardException {		List<CSVDataModel> mockedModelList =  new ArrayList<>();		mockedModelList.add(CSVDataModel.builder().country_Region("IN").province_State("MH").active("100").build());		mockedModelList.add(CSVDataModel.builder().country_Region("IN").province_State("DL").active("80").build());		Mockito.when(csvParser.buildCsvDataModel("DummyURL")).thenReturn(mockedModelList);		List<CSVDataModel> modelList = csvParser.buildCsvDataModel("DummyURL");		assertNotNull(modelList);		assertEquals(2, modelList.size());	}}